/***** Draw ****/
void DocumentView::Draw(BRect updateRect)
{


	
	CTextView::Draw(updateRect);
	
	StrokeRect( BRect( TextRect().left -2,
	                   TextRect().top -2,
	                   TextRect().right +2,
	                   TextRect().bottom +2 )
	          );
		
}

