/***** KeyDown ****/
void DocumentView::KeyDown(const char *bytes,int32 numBytes)
{
	CTextView::KeyDown( bytes, numBytes);

	Invalidate();
}

/***** KeyUp ****/
void DocumentView::KeyUp(const char *bytes,int32 numBytes)
{
	CTextView::KeyUp( bytes, numBytes);
	Invalidate();
}
